body {
  background-color: #000000;
}

.container {
  width: 960px;
  margin: 0 auto; /* center */
  background-color: #ebebe0;
}

.header,
.content__left,
.content__right,
.footer {
  float: left;
  padding: 1em;
  height: 100%;
}

.header,
.footer {
  width:  100%;
  height: 100%;
  background-color: #ebebe0;
}

.content {
  float: left;
  height:  100%;
  width:  100%;
  background-color: #ccccb3;
}

.content__left {
  width:  65%;
  background-color: #ccccb3;
}

.content__right {
  width:  35%;
  background-color: #b8b894; 
}

@media screen and (max-width: 960px) {

  .container,
  .content__left,
  .content__right  {
    width:  100%;
  }

}
